    const [showDropDownMobile , setshowDropDownMobile ] = useState(false)

    return (
        <div className='z-[1000] relative  '>
            <nav className={`fixed top-0 left-[50%] translate-x-[-50%] w-full transition-all  duration-300 z-50  ${isTransparent ? 'bg-transparent' : `backdrop-blur-md bg-fixed bg-cover bg-center bg-secondery shadow-md`}`}>
                <div className=' container h-[80px] max-lg:px-[40px] flex justify-between items-center py-3  '>
                    <Link href='/' className='text-2xl font-bold'>
                        <Image className={` ${!isTransparent ? ' ml-0 mr-0 ' : 'xl:ltr:ml-[100px] xl:rtl:mr-[100px]'}  duration-300 max-md:w-[85px]  w-[145px]  object-contain`} width={145} height={45} alt='' src={ isAllowed ?( !isVisible ? "/imgs/logo.png" :  '/imgs/logo.png') : "/imgs/logo2.png" } />
                    </Link>

                    <ul className='hidden min-[1200px]:flex gap-[50px] items-center'>
                        <div className='flex gap-[28px] items-center '>
                            {links.map(link => (
                                <Link key={link.value} href={link.value} className={` group flex items-center gap-[5px]  text-lg font-normal relative after:absolute after:left-0 after:bottom-[-10px] after:duration-300 after:h-[2px] after:bg-primary after:w-[0px] ${pathname === link.value ? 'text-primary after:!w-full ' : `${ navIsWhite ? "text-white" : (isTransparent ? 'text-[#57677A] ' : 'text-white')}`}`}>
                                    {link.name}
                                    {link.key == "services" && <ChevronUp className=' rotate-[-180deg] scale-[.8] duration-300 group-hover:rotate-0  '  />}

                                    { link.key == "services" && 
                                    <div className="absolute rtl:right-0 ltr:left-0 top-full mt-2 w-[220px] max-h-0 opacity-0 bg-white shadow-lg rounded-lg z-10 overflow-hidden transition-all duration-300 ease-in-out   group-hover:max-h-[300px] group-hover:opacity-100">
                                        {Services.map(service => (
                                            <Link key={service.href} href={service.href} className="block min-h-[60px]  px-2 text-gray-700 hover:bg-gray-100">
                                                <div className="flex items-center  gap-2 h-full "> 
                                                    <span className=' scale-[.5] w-[50px] ' > {service.icon}  </span>
                                                    <span className=' ltr:!text-[14px] leading-[20px] rtl:!text-[16px]  ' >{service.name}</span> 
                                                </div>
                                            </Link>
                                        ))}
                                     </div>}

                                </Link>
                            ))}
                            <Translate cn={`${ navIsWhite ? "text-white stroke-white" : isTransparent ? 'text-[#57677A] ' : 'text-white stroke-white ' }`} />
                        </div>
                        <Button cn={"hover:!bg-primary hover:bg-opacity-80  "}  shadow={false } icon={<ArrowRight  />} name={t('login')} href='/login' />
                    </ul>

                    <X cnPath={`stroke-black group-hover:stroke-primary duration-300 max-md:stroke-white ${ navIsWhite ? "!stroke-white" : "stroke-black" } ${isTransparent ? "stroke-black" : "stroke-white" } `} cn={' group min-[1200px]:hidden w-[24px] h-[24px] flex items-center cursor-pointer'} onClick={toggleMenu} />
                </div>
            </nav>

            <div className={`fixed top-0 left-0 w-full h-full z-50 bg-black bg-opacity-90 transition-all duration-300 custom-cursor  rtl:scale-x-[-1] backdrop-blur-[5px] ${isMenuOpen ? 'square-100' : 'square-0'}`} onClick={toggleMenu}></div>

            <div className={`${isMenuOpen ? 'ltr:left-0 rtl:right-0' : 'ltr:left-[-400px] rtl:right-[-400px]'} fixed top-0 z-[1000] w-full max-w-[350px] h-full p-5 shadow-lg bg-cover bg-center bg-secondery/70 backdrop-blur-[10px] transition-all duration-300 `}>
                <div className='flex py-[20px] items-center justify-between'>
                    <Link href='/' className='text-2xl font-bold'> <Image className='xl:ltr:ml-[100px] xl:rtl:mr-[100px] max-md:w-[80px] w-[115px] object-contain' width={145} height={45} alt='Logo' src={'/imgs/logo.png'} /> </Link>
                    <Close className='text-white scale-[1.2] cursor-pointer hover:scale-[1.3] duration-300' onClick={toggleMenu} />
                </div>
                <hr className='w-[90%] mx-auto border-t-white border-t-[1px] my-[20px] opacity-30' />
                <ul className='flex  flex-col gap-[10px] pt-[10px]  '>
                    {links.map(link => (
                        <Link key={link.value} href={ link.key != "services" ? link.value : "" } onClick={ ()=> {link.key != "services" && toggleMenu() ; link.key == "services" && setshowDropDownMobile(!showDropDownMobile) }} className={` group flex-col  rounded-[5px] flex items-center justify-between  rtl:mr-[20px] ltr:ml-[20px] ${link.key != "services" && "hover:bg-primary" } hover:bg-primary duration-300 t20 capitalize !font-[500] text-white ${pathname === link.value ? 'bg-primary  ' : ''}`}>
                            <div className='w-full flex  py-[10px] px-[15px]  justify-between items-center ' >
                                <div className='flex items-center gap-[15px]  ' >
                                    {link.icon}
                                    {link.name}
                                </div>

                                {link.key == "services" && <ChevronUp className={`rotate-[-180deg] scale-[.8] duration-300 ${showDropDownMobile && "rotate-0"} `}  />}
                            </div>
                            
                            { link.key == "services" && 
                            <div className={` ${showDropDownMobile ? " ltr:pl-[15px] rtl:pr-[15px] max-h-[500px] opacity-100" : "max-h-0 "}  !shadow-none group-hover:bg-[#0d2f55] w-full  z-10 overflow-hidden transition-all duration-300 ease-in-out  `}>
                                {Services.map(service => (
                                    <Link onClick={toggleMenu} key={service.href} href={service.href} className="block h-[50px]  px-2 text-white text-opacity-70 ">
                                        <div className="flex items-center  gap-2 h-full "> 
                                            <span className=' scale-[.5] w-[50px] ' > {service.icon}  </span>
                                            <span className=' ltr:!text-[14px] leading-[20px] rtl:!text-[16px]  ' >{service.name}</span> 
                                        </div>
                                    </Link>
                                ))}
                                </div>}
                        </Link>
                    ))}
                    <Translate cnParent={"flex-row-reverse !gap-[10px] !justify-end px-[30px] "} cn={"text-white stroke-white   "} />
                    <Button icon={<ArrowRight  />} cn={'mt-[50px] absolute bottom-[20px] left-[20px] !shadow-none hover:!bg-opacity-80 hover:!bg-primary '} name={t('login')} href='/login' />
                </ul>
            </div>

        </div>
    );
